import React from 'react';
import './App.css';
import Header from './components/header/Header';
import Body from './components/body/Body';
import Footer from './components/footer/Footer';

class App extends React.Component {

  constructor(props){
    super(props);


    this.setState = {
      activeTab: -1
    }

    this.changeTab = this.changeTab.bind(this);
  }

  changeTab(tab){

    if (this.state.activeTab === tab)
    return

    switch (tab) {
      case "/all-items":
        tab = 1;
        break;
      case "/pending-items":
        tab = 2;
        break;
      case "/active-items":
        tab = 3;
        break;
    }
  }

  render() {
    return(
      <div id='app-container'>
        <Header changeTab={this.changeTab}></Header>
        <Body></Body>
        <Footer></Footer>
    </div>
    )
  }
}

export default App;
