import React from 'react';
import './App.css';
import Header from './components/header/Header';
import Body from './components/body/Body';
import Footer from './components/footer/Footer';

class App extends React.Component {

  constructor(props){
    super(props);

    this.state = {
      activeTab: -1
    }

    this.changeTab = this.changeTab.bind(this);
    this.examinePath = this.examinePath.bind(this);

  }

  componentDidMount(){
    this.examinePath();
  }

  examinePath(){

    switch (window.location.pathname){
      case "/all-items":
      case "":
      default:
        this.setState({activeTab: 1});
        break;
      case "/pending-items":
      case 2:
        this.setState({activeTab: 2});
        break;
      case "/completed-items":
      case 3:
        this.setState({activeTab: 3});
        break;
    }
  }
  changeTab(tab){

    if (this.state.activeTab === tab)
    return

    switch (tab) {
      case 1:
      default:
        this.setState({activeTab: 1});
        window.history.pushState(null, "Tab1", "/all-items");
        break;
      case 2:
        this.setState({activeTab: 2});
        window.history.pushState(null, "Tab2", "/pending-items");
        break;
      case 3:
        this.setState({activeTab: 3});
        window.history.pushState(null, "Tab3", "/completed-items");
        break;
    }
  }

  render() {
    return(
      <div id='app-container'>
        <Header changeTab={this.changeTab} tab={this.activeTab}></Header>
        <Body tab={this.state.activeTab}></Body>
        <Footer></Footer>
    </div>
    )
  }
}

export default App;
